export default {
  getfrequencyData: (tabData) => {
    if (!tabData || tabData.length === 0) {
      // Return an empty array if tabData is undefined or empty
      return [];
    }

    let bins = {}; 
    // split marks into bins with size of 1
    tabData.forEach(mark => {
      let bin = Math.floor(mark);  // Bin size of 1, round the mark value

      // bin counter++ if existed; else, create entry and set this counter to 0
      bins[bin] = (bins[bin] || 0) + 1; 
    });

    // Convert bins object to an array format for the chart
    return Object.entries(bins).map(([scoreRange, count]) => ({
      x: `${scoreRange}`, // keys (bin values)
      y: count // values (frequency)
    }));
  }
}

