export default {
  getFilteredAverage: (tabData, questionId, selectedCruiseID) => {
    if (!tabData || tabData.length === 0 || !selectedCruiseID) {
      return null;
    }

    let total = 0;
    let count = 0;

    for (let i = 0; i < tabData.length; i++) {
      let row = tabData[i];

      // Parse survey if needed
      let survey = typeof row.survey === "string" ? JSON.parse(row.survey) : row.survey;

      if (
        row.question?.questionId === questionId &&
        survey?.cruiseID === selectedCruiseID
      ) {
        let value = parseFloat(row.response);
        if (!isNaN(value)) {
          total += value;
          count += 1;
        }
      }
    }

    if (count === 0) return null;
    return total / count;
  }
}

