export default {
	loginUser: async (username, password) => {
		try {
			let response = await user_login.run({
				username: username,
				password: password
			});

			if (response.status === "success") {
				if (response.isAdmin === false) {
					storeValue("username", response.username); 
					storeValue("isAuthenticated", true); 
					storeValue("isAdmin", response.isAdmin);
					showAlert("Login successful!", "success");
					navigateTo("Survey");
				} else {
					showAlert("Admins cannot log in as customers. Please use the admin portal.", "error");
				}
			} else {
				showAlert("Invalid username or password", "error");
			}
		} catch (error) {
			showAlert("An error occurred during login. Please try again.", "error");            
		}
	},

	// logout
	logoutUser: () => {
		storeValue("username", undefined); 
		storeValue("isAuthenticated", false);
		storeValue("isAdmin", false);
		showAlert("Logged out successfully!", "info");
		navigateTo("Login");
	},
};

